class Ch2Comp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isActive: false, // This is the 'property' you want to change
      count: 0,
    };
    // Bind the event handler to the component instance
    this.handleClick = this.handleClick.bind(this);
  }
}

export default Ch2Comp;
